sys_script_include.script:
var getData = Class.create();
getData.prototype = {
    initialize: function() {
        CITY_TABLE = "x_141565_helloworl_city";

        var cities = new GlideRecord(CITY_TABLE);
        cities.query();

        while (cities.next()) {
            var restMessage = new sn_ws.RESTMessageV2("Weather", "GET");
            restMessage.setQueryParameter("q", cities.name);
            restMessage.setQueryParameter("APPID", gs.getProperty("x_141565_helloworl.REST Data"));
            var response = restMessage.execute();
            var responseBody = response.getBody();
            var httpsStatus = response.getStatusCode();

            if (httpsStatus == 200) {
                var parsedResponse = JSON.parse(responseBody);



                var stagingTable = new GlideRecord("x_141565_helloworl_staging_weather_data");
                stagingTable.initialize();
				stagingTable.setValue("city_name",parsedResponse.name);
				stagingTable.setValue("clouds".parsedResponse.clouds.all);
				stagingTable.setValue("humidity".parsedResponse.main.humidity);
				stagingTable.setValue("pressure".parsedResponse.main.pressure);
				stagingTable.setValue("temperature".parsedResponse.main.temp);
				stagingTable.setValue("wind_speed".parsedResponse.wind.speed);
				stagingTable.update();

            }
			else {gs.log("Error");}

        }

    },

    type: 'getData'
};
