sys_script_include.script:
var getData = Class.create();
getData.prototype = {
    initialize: function() {

    },
    process: function() {
        CITY_TABLE = "x_141565_helloworl_city";
        var cityGr = new GlideRecord(CITY_TABLE);
        cityGr.query();
          //Initialize and Create the Very important Import Set  
         var importSet = new GlideRecord('sys_import_set');
        importSet.short_description = 'Import set from scripted rest api';
        importSet.table_name = "x_141565_helloworl_staging_weather_data";
        var importSetID = importSet.insert();

        while (cityGr.next()) {
            var restMessage = new sn_ws.RESTMessageV2("Weather", "GET");
            restMessage.setQueryParameter("q", cityGr.name);
            restMessage.setQueryParameter('APPID', gs.getProperty('x_141565_helloworl.Data'));
            var response = restMessage.execute();
            var responseBody = response.getBody();

            var stagingGr = new GlideRecord("x_141565_helloworl_staging_weather_data");

            //   gs.info(response.getStatusCode);
            //  gs.info(responseBody);

            if (response.getStatusCode() == 200) {

                var parsedResponse = JSON.parse(responseBody);
                stagingGr.initialize();

                for (var i in parsedResponse) {
                    if (typeof parsedResponse[i] != "object") {
                        stagingGr.setValue("" + i, parsedResponse[i]);
                    } else {
                        for (var j in parsedResponse[i]) {
                            stagingGr.setValue("" + j, parsedResponse[i][j]);
                        }
                    }
                }
                stagingGr.insert();

            } else {
                gs.log("Error getting weather data for city " + cityGr.getValue("u_city_name") + ". Response status code: " + response.getStatusCode());
            }
        }
     //Configure and Run the Transform 
        var transformMapId = 'd146053647a22110f35e7ac8f36d43cc';
        var transformer = new GlideImportSetTransformer();
        transformer.setMapID(transformMapId);
        transformer.transformAllMaps(importSet);

    },

    type: 'getData'
};
